                                </div>
                                <div className="form-group">
                                    <input type="email" id="email" placeholder="Email" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" id="phone" placeholder="Phone" className="form-control"/>
                                </div>
