import React, { Component } from "react";
import {
    Container, Row, Col
} from 'react-bootstrap';
import emailjs from 'emailjs-com';
import { init } from 'emailjs-com';
init("user_1I17qEirtZkLAh5ed5qOv");

class Demo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            email: "",
            feedback: "",
        };
    }
    handleInputChange(event) {
        event.preventDefault();
        const target = event.target;
        const name = target.name;
        const value = target.value;
        this.setState({ [name]: value });
    }

    render() {
        return (
            <div className="component_content demo">
                <Container>
                    <Row>
                        <Col xs={12} xl={6}>
                            <form
                                className="ui form"
                                id={this.props.id}
                                name={this.props.name}
                                method={this.props.method}
                                action={this.props.action}
                            >
                                <textarea
                                    id="name"
                                    name="name"
                                    onChange={this.handleInputChange.bind(this)}
                                    placeholder="your name"
                                    required
                                    value={this.state.name}
                                    style={{ width: "100%" }}
                                    rows={1}
                                />
                                <br />
                                <textarea
                                    id="email"
                                    name="email"
                                    onChange={this.handleInputChange.bind(this)}
                                    placeholder="your email address"
                                    required
                                    value={this.state.email}
                                    error={this.state.errors.email}
                                    style={{ width: "100%" }}
                                    rows={1}
                                />
                                <br />
                                <textarea
                                    id="feedback"
                                    name="feedback"
                                    onChange={this.handleInputChange.bind(this)}
                                    placeholder="what would you like to chat about?"
                                    required
                                    value={this.state.feedback}
                                    style={{ width: "100%", height: "250px" }}
                                />
                                <br />
                                <input
                                    type="button"
                                    value="Send"
                                    className="ui button"
                                    style={{
                                        fontFamily: "Amatic SC",
                                        fontSize: "20px",
                                        color: "blue"
                                    }}
                                    onClick={this.sendMessage.bind(this)}
                                />
                            </form>

                            {/* <h2 className="text-blue">Get a demo</h2>
                            <form>
                                <div className="form-group">
                                    <input type="text" id="name" placeholder="Name" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" id="yacht" placeholder="Yacht" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="email" id="email" placeholder="Email" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" id="phone" placeholder="Phone" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" id="location" placeholder="Location" className="form-control"/>
                                </div>
                                    <button type="submit" className="btn btn-primary">
                                        Submit
                                    </button>
                                </form> */}
                        </Col>
                        <Col xs={12} xl={6}>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }

    sendMessage(event) {
        event.preventDefault();
        if (!this.validateMail()) {
            return;
        }
        const templateParams = {
            from_name: this.state.name + " (" + this.state.email + ")",
            to_name:  'valykwsayz1990@gmail.com',
            feedback: this.state.feedback
        };
        emailjs
            .send("gmail", "portfoliositecontact", templateParams,  'user_1I17qEirtZkLAh5ed5qOv' )
            .then(
                function (response) {
                    toast.success("Your message has successfully sent!", {
                        position: toast.POSITION.BOTTOM_CENTER
                    });
                    console.log("SUCCESS!", response.status, response.text);
                },
                function (err) {
                    toast.error("Your message was not able to be sent");
                }
            );
        this.setState({
            name: "",
            email: "",
            feedback: ""
        });
    }
}

export default Demo;
