import React, { Component } from "react";
import './App.scss';
import Home from "./Home";
import About from "./About";
import Overview from "./Overview";
import Demo from "./Demo";
import Contact from "./Contact";
import Footer from "./Footer";
import 'bootstrap/dist/css/bootstrap.min.css';
import { SectionsContainer, Section } from 'react-fullpage';

class HomePage extends Component {

    render() {
        if (window.innerWidth < 1200) {
            var scroll = true;
        } else {
            scroll = false;
        }

        let options = {
            sectionClassName: 'section',
            anchors: ['home', 'about', 'overview', 'getademo', 'contactus', 'footer'],
            // responsiveWidth: 1200,
            // scrollBar: scroll,
            // navigation: false,
            // // verticalAlign: true,
            // verticalCentered: true,
            // sectionPaddingTop: '95px',
            // arrowNavigation: true,
            // loopBottom: true,
            // continuousVertical: true,
            // normalScrollElements: '#home',
            css3: true,
            autoScrolling: true,
            fitToSection: true,
            fitToSectionDelay: false,
            scrollBar: true,
            easing: 'easeInOutCubic',
            easingcss3: 'ease',
            animateAnchor: true,
            loopBottom: true,
            loopTop: false,
            loopHorizontal: true,
            continuousVertical: true,
            continuousHorizontal: false,
            scrollHorizontally: false,
            interlockedSlides: false,
            resetSliders: false,
            fadingEffect: true,
            normalScrollElements: '#somesectionsID',
            scrollOverflow: false,
            scrollOverflowOptions: true,
            touchSensitivity: 5,
            normalScrollElementTouchThreshold: 15,
            bigSectionsDestination: null,

            /*My additions below this point*/

            showActiveTooltip: true,

        };

        return (
            <SectionsContainer className="container" {...options}>
                <Section className="home_section">
                    <Home/>
                </Section>
                <Section className="about_section">
                    <About/>
                </Section>
                <Section className="overview_section">
                    <Overview/>
                </Section>
                <Section className="demo_section">
                    <Demo/>
                </Section>
                <Section className="contact_section">
                    <Contact/>
                </Section>
                <Section className="footer_section">
                    <Footer/>
                </Section>
            </SectionsContainer>
        );
    }
}

export default HomePage;
