import React, { Component } from "react";
import {
    Container, Row, Col
} from 'react-bootstrap';
// import { emailjs , toast } from 'emailjs-com';
import emailjs from 'emailjs-com';
import { init } from 'emailjs-com';
init("user_1I17qEirtZkLAh5ed5qOv");

class Demo extends Component {



    render() {
        return (
            <div className="component_content demo">
                <Container>
                    <Row>
                        <Col xs={12} xl={6}>
                            <form className="feedback-form" onSubmit={handleSubmit}>
                                <h1>Your Feedback</h1>
                                <textarea
                                    className="text-input"
                                    id="feedback-entry"
                                    name="feedback-entry"
                                    onChange={handleChange}
                                    placeholder="Enter your feedback here"
                                    required
                                    value={feedback}
                                />
                                <div className="btn-group">
                                    <button className="btn btn--cancel" onClick={handleClose}>
                                        Cancel
                                    </button>
                                    <input type="submit" value="Submit" className="btn btn--submit" />
                                </div>
                            </form>

                            {/* <h2 className="text-blue">Get a demo</h2>
                            <form>
                                <div className="form-group">
                                    <input type="text" id="name" placeholder="Name" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" id="yacht" placeholder="Yacht" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="email" id="email" placeholder="Email" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" id="phone" placeholder="Phone" className="form-control"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" id="location" placeholder="Location" className="form-control"/>
                                </div>
                                    <button type="submit" className="btn btn-primary">
                                        Submit
                                    </button>
                                </form> */}
                        </Col>
                        <Col xs={12} xl={6}>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }


    handleSubmit(event) {
        event.preventDefault()

        const {
            REACT_APP_EMAILJS_RECEIVER: receiverEmail,
            REACT_APP_EMAILJS_TEMPLATEID: template,
            REACT_APP_EMAILJS_USERID: user
        } = this.props.env

        this.sendFeedback(
            template,
            this.props.senderEmail,
            receiverEmail,
            this.state.feedback,
            user
        )

        this.setState({
            formSubmitted: true
        })
    }

    sendFeedback(templateId, senderEmail, receiverEmail, feedback, user) {
        window.emailjs.send(
            'default_service', // default email provider in your EmailJS account
            templateId,
            {
                senderEmail,
                receiverEmail,
                feedback
            },
            user
        )
            .then(res => {
                this.setState({ formEmailSent: true })
            })
            // Handle errors here however you like, or use a React error boundary
            .catch(err => console.error('Failed to send feedback. Error: ', err))
    }
}

export default Demo;
