import React, { Component } from "react";
import './App.scss';
import {
    BrowserRouter
} from "react-router-dom";
import Home from "./Home";
import About from "./About";
import Overview from "./Overview";
import Demo from "./Demo";
import Contact from "./Contact";
import logo from './images/Logo/YMP small logo no bg.png';
import user from './images/Logo/user-solid.svg';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Container, Navbar, Nav,
} from 'react-bootstrap';
import { SectionsContainer, Section } from 'react-fullpage';

class Header extends Component {

    render() {
        if(window.screen.width < 900) {
            var scroll = true;
        } else {
            scroll = false;
        }

        let options = {
            sectionClassName: 'section',
            anchors: ['home', 'about', 'overview', 'demo', 'contact'],
            responsiveWidth: 900,
            scrollBar: scroll,
            navigation: true,
            verticalAlign: true,
            sectionPaddingTop: '95px',
            arrowNavigation: true,
        };

        return (
            <BrowserRouter>
                <div className="App-header">
                    <Navbar fixed="top" collapseOnSelect expand="lg" variant="dark" justify-content-between="true">
                        <Container>
                                <Navbar.Brand href="/">
                                    <img src={logo} className="App-header-logo" alt="logo" />
                                    <span className="App-header-text">yacht maintenance program</span>
                                </Navbar.Brand>
                            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                                <Navbar.Collapse id="responsive-navbar-nav" className="justify-content-end">
                                    <Nav className="mr-auto mr-sm-2">
                                        <Nav.Link href="#home">Home</Nav.Link>
                                        <Nav.Link href="#about">About Us</Nav.Link>
                                        <Nav.Link href="#overview">Overview</Nav.Link>
                                        <Nav.Link href="#demo">Get A Demo</Nav.Link>
                                        <Nav.Link href="#contact">Contact Us</Nav.Link>
                                        <Nav.Link className="singin" href="https://ymponline.com/">
                                            <img src={user} className="user-logo" alt="logo" />Signin
                                        </Nav.Link>
                                    </Nav>
                                </Navbar.Collapse>
                            </Container>
                        </Navbar>
                </div>
                <div className="App-content">
                    <SectionsContainer className="container" {...options}>
                        <Section className="home_section">
                            <Home/>
                        </Section>
                        <Section className="about_section">
                            <About/>
                        </Section>
                        <Section className="overview_section">
                            <Overview/>
                        </Section>
                        <Section className="demo_section">
                            <Demo/>
                        </Section>
                        <Section className="contact_section">
                            <Contact/>
                        </Section>
                    </SectionsContainer>
                </div>
            </BrowserRouter>
        );
    }
}

export default Header;
