import React, { Component } from "react";
import './App.scss';
import {
    BrowserRouter
} from "react-router-dom";
import Home from "./Home";
import About from "./About";
import Overview from "./Overview";
import Demo from "./Demo";
import Contact from "./Contact";
import logo from './images/Logo/YMP small logo no bg.png';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Container, Row, Col, Navbar, Nav,
} from 'react-bootstrap';

class Header extends Component {
    refA = React.createRef()
    refB = React.createRef()
    refC = React.createRef()
    refD = React.createRef()
    refE = React.createRef()

    componentDidMount() {
        this.setState({ loaded: true })
    }

    handleScrollTo = (elRef) => {
        // Incase the ref supplied isn't ref.current
        const el = elRef.current ? elRef.current : elRef

        // Scroll the element into view
        el.scrollIntoView({
            behavior: 'smooth',
            block: 'start'
        })
    }

    render() {
        return (
            <BrowserRouter>
                <div className="App-header">
                    <Navbar fixed="top" collapseOnSelect expand="md" bg="dark" justify-content-between="true">
                    <Container fluid>
                        <Row>
                            <Col>
                                <Navbar.Brand href="/">
                                    <img src={logo} className="App-header-logo" alt="logo" />
                                    <span className="App-header-text">yacht maintenance program</span>
                                </Navbar.Brand>
                                <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                                <Navbar.Collapse id="responsive-navbar-nav" className="justify-content-end">
                                    <Nav className="mr-auto">
                                        <Nav.Link.Collapse onClick={() => { this.handleScrollTo(this.refA) }}>Home</Nav.Link.Collapse>
                                        <Nav.Link onClick={() => { this.handleScrollTo(this.refB) }}>About Us</Nav.Link>
                                        <Nav.Link onClick={() => { this.handleScrollTo(this.refC) }}>Overview</Nav.Link>
                                        <Nav.Link onClick={() => { this.handleScrollTo(this.refD) }}>Get A Demo</Nav.Link>
                                        <Nav.Link onClick={() => { this.handleScrollTo(this.refE) }}>Contact Us</Nav.Link>
                                        <Nav.Link href="https://ymponline.com/">Signin</Nav.Link>
                                    </Nav>
                                </Navbar.Collapse>
                            </Col>
                        </Row>
                    </Container>
                                </Navbar>
                </div>
                <div className="App-content">
                    <Container>
                        <Row>
                            <Col>
                                <div className="route_element" ref={this.refA}>
                                    <Home/>
                                </div>
                                <div className="route_element" ref={this.refB}>
                                    <About/>
                                </div>
                                <div className="route_element" ref={this.refC}>
                                    <Overview/>
                                </div>
                                <div className="route_element" ref={this.refD}>
                                    <Demo/>
                                </div>
                                <div className="route_element" ref={this.refE}>
                                    <Contact/>
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </div>
            </BrowserRouter>
        );
    }
}

export default Header;
