import React, { Component } from "react";
import {
    Container, Row, Col
} from 'react-bootstrap';
import { init } from 'emailjs-com';
init("user_1I17qEirtZkLAh5ed5qOv");

const btn = document.getElementById('button');

document.getElementById('form')
    .addEventListener('submit', function (event) {
        event.preventDefault();

        btn.value = 'Sending...';

        const serviceID = 'default_service';
        const templateID = 'template_2qz531s';

        emailjs.sendForm(serviceID, templateID, this)
            .then(() => {
                btn.value = 'Send Email';
                alert('Sent!');
            }, (err) => {
                btn.value = 'Send Email';
                alert(JSON.stringify(err));
            });
    });

class Demo extends Component {
    render() {
        return (
            <div className="component_content demo">
                <Container>
                    <Row>
                        <Col xs={12} xl={6}>
                            <form id="form">
                                <div class="field">
                                    <label for="from_name">from_name</label>
                                    <input type="text" name="from_name" id="from_name"/>
  </div>
                                    <div class="field">
                                        <label for="to_name">to_name</label>
                                        <input type="text" name="to_name" id="to_name"/>
  </div>
                                        <div class="field">
                                            <label for="message">message</label>
                                            <input type="text" name="message" id="message"/>
  </div>
                                            <div class="field">
                                                <label for="reply_to">reply_to</label>
                                                <input type="text" name="reply_to" id="reply_to"/>
  </div>

                                                <input type="submit" id="button" value="Send Email" />
</form>

                                                <script type="text/javascript"
                                                    src="https://cdn.jsdelivr.net/npm/emailjs-com@2/dist/email.min.js"></script>

                                                <script type="text/javascript">
                                                    emailjs.init('user_1I17qEirtZkLAh5ed5qOv')
</script>

                            <h2 class="text-blue">Get a demo</h2>
                            <form>
                                <div class="form-group">
                                    <input type="text" id="name" placeholder="Name" class="form-control"/>
                                </div>
                                <div class="form-group">
                                    <input type="text" id="yacht" placeholder="Yacht" class="form-control"/>
                                </div>
                                <div class="form-group">
                                    <input type="email" id="email" placeholder="Email" class="form-control"/>
                                </div>
                                <div class="form-group">
                                    <input type="text" id="phone" placeholder="Phone" class="form-control"/>
                                </div>
                                <div class="form-group">
                                    <input type="text" id="location" placeholder="Location" class="form-control"/>
                                </div>
                                    <button type="submit" class="btn btn-primary">
                                        Submit
                                    </button>
                                </form>
                        </Col>
                        <Col xs={12} xl={6}>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Demo;
